SET NOCOUNT ON;
DECLARE @CONTACT_DETAIL table(COMPANY_CONTACT_ROLE nvarchar(MAX));

insert into @CONTACT_DETAIL
SELECT TOP(1) COMPANY_CONTACT_ROLE FROM dbo.PMS_CONTACT_PERSON_DETAIL_LM WHERE PAIRED_COMPANY_ID={{appsmith.URL.queryParams[Configs.editCompany]}} 
AND PAIRED_CONTACT_ID={{appsmith.URL.queryParams[Configs.editContacePerson]}}

if ((SELECT COUNT(*) FROM @CONTACT_DETAIL)=0)
insert into @CONTACT_DETAIL
SELECT TOP(1) COMPANY_CONTACT_ROLE FROM dbo.PMS_CONTACT_PERSON_DETAIL_TEMP WHERE 
SESSION_TOKEN = {{appsmith.store[Configs.userSession].TOKEN}}
AND PAIRED_CONTACT_ID={{appsmith.URL.queryParams[Configs.editContacePerson]}};

SELECT * FROM @CONTACT_DETAIL
