actionConfiguration.body:
IF {{DefaultInventory.INVENTORY_ID.data === DefaultInventory.PRODUCT_ID.data?1:0}} = 1
SELECT TOP 1 PICTURE_CATALOG_FILE as PICTURE_INVENTORY_FILE FROM dbo.PMS_CATALOG_PICTURE WHERE PICTURE_CATALOG_ID= {{DefaultInventory.PRODUCT_ID.data}}

ELSE
SELECT TOP 1 PICTURE_INVENTORY_FILE FROM dbo.PMS_INVENTORY_PICTURE WHERE PICTURE_INVENTORY_ID= {{DefaultInventory.INVENTORY_PICTURE.data}}
